/* ====== kr.co.sitglobal.oklms.lu.member.service.impl.MemberMapper.listPlanResultMemberStd ====== */ select a.YYYY, a.TERM, a.SUBJECT_CODE, a.CLASS as subClass, FN_GETSUBJECT_NAME(a.YYYY, a.TERM, a.SUBJECT_CODE, a.CLASS) as subjectName, d.HRD_TRANING_NAME, f.SUBJECT_TRANING_TYPE, e.MEM_SEQ, e.MEM_ID, e.mem_name, e.hp_no1, e.hp_no3, e.email, SUBSTR(e.email,1,1) as email1, SUBSTR(e.email,(INSTR(e.email, '@')+0)) email2, f.SUBJECT_TRANING_TYPE, f.DEPARTMENT_NAME, f.SUBJECT_NAME, if(a.DELETE_YN = 'N', '재학', '제적') as deleteName, case when a.DELETE_YN = 'Y' then DATE_FORMAT (a.UPDATE_DATE, '%Y.%m.%d') end deleteDt, e.DELETE_YN, FN_GETCOMPAYNAME(#{companyId}) as companyName, CASE WHEN SUBSTRING(FN_GETUSERID(a.MEM_SEQ),1,2) != '20' THEN DATE_FORMAT(NOW(),'%Y')+1-SUBSTRING(CONCAT('20',FN_GETUSERID(a.MEM_SEQ)),1,4) ELSE DATE_FORMAT(NOW(),'%Y')+1-SUBSTRING(FN_GETUSERID(a.MEM_SEQ),1,4) END SUBJECT_GRADE from LMS_LESSON a, ( select b.YYYY, b.TERM, b.SUBJECT_CODE, b.CLASS, c.HRD_TRANING_NAME from lms_traning_process c , lms_traning_process_mapping b where 1 = 1 and c.COMPANY_ID = b.COMPANY_ID and c.TRANING_PROCESS_ID = b.TRANING_PROCESS_ID and c.COMPANY_ID = #{companyId} and c.DELETE_YN = 'N' and b.DELETE_YN = 'N' )d, com_member e, lms_subject f where 1 = 1 and a.YYYY = d.YYYY and a.TERM = d.TERM and a.SUBJECT_CODE = d.SUBJECT_CODE and a.CLASS = d.CLASS and a.MEM_SEQ = e.MEM_SEQ and a.YYYY = f.YYYY and a.TERM = f.TERM and a.SUBJECT_CODE = f.SUBJECT_CODE and a.CLASS = f.CLASS
