               MEM_SEQ
          FROM COM_MEMBER
          WHERE MEM_ID = #{memId}

mapper.delete:
	/* ====== kr.co.sitglobal.oklms.lu.member.service.impl.MemberMapper.deleteMember ========= */
	UPDATE LMS_SUBJ_TUT_MAPPING
	SET
	   UPDATE_DATE = NOW()
	  ,UPDATE_USER = IFNULL(#{sessionMemSeq} , '2009USER0000001')
	  ,DELETE_YN = 'Y'
	WHERE 1 = 1
	  AND YYYY = #{yyyy}
      AND TERM = #{term}
      AND SUBJECT_CODE = #{subjectCode}
      AND CLASS = #{subClass}
      AND MEM_SEQ = #{memSeq}

/* ====== kr.co.sitglobal.oklms.lu.member.service.impl.MemberMapper.deleteMemberList ====== */ UPDATE LMS_SUBJ_TUT_MAPPING SET UPDATE_DATE = NOW() ,UPDATE_USER = #{sessionMemSeq} ,DELETE_YN = 'Y'
WHERE MEM_SEQ IN
'${delMemSeqIds}'


